package week1.day1;

public class LearnIfandFor {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
        // Problem statement: print all the even numbers between 1 and 50
		
		// 1. Apply the condition to check the even number (IF)
		// 2. Check multiple number of times (FOR)
		
		
		for (int i = 1; i <= 50 ; i++) {
			
			if (i%2 == 0) {
				//System.out.println(" It is an even number " + i);
				System.out.println(i + " is an even number");
			}
			
		}
		
	}

}
